const NewQuote = () => {
  const {sendRequest, status } = useHTTP(addQuote)
    const history = useHistory();


  const addQuoteHandler = (quoteData) => {
    sendRequest(quoteData)

    history.push('./quotes')
  };

  return <QuoteForm onAddQuote={addQuoteHandler} />;
};

